mapper.select:
SELECT

FROM oa_feedback a

WHERE a.id = #{id}

SELECT
FROM oa_feedback a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.create_by LIKE '%'||#{createBy.id}||'%' '%'+#{createBy.id}+'%' concat('%',#{createBy.id},'%')
AND a.office_id = #{officeInfo.id}
AND a.sku = #{sku}
AND a.content LIKE '%'||#{content}||'%' '%'+#{content}+'%' concat('%',#{content},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT

FROM oa_feedback a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE oa_feedback SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
